import React, { useState, useEffect, useRef, useContext } from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import { List, Button, Modal, Header, Icon } from "semantic-ui-react";

import "./question.css";

function Question() {
  const QUESTION_API = gql`
    {
      questions {
        id
        description
        answer {
          id
          option
          isCorrect
        }
      }
    }
  `;

  // Run and fetch the data on the query
  const { loading, data: { questions: Quest } = {} } = useQuery(QUESTION_API);

  if (Quest) {
    console.log(Quest);
  }


 

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showAnswer, setshowAnswer] = useState(false);
  const [ final, setFinal] = useState(false);
  const [answerCorrect, setAnswerCorrect] = useState('');
  const [ open, setOpen ] = useState(false);


  const [seconds, setSeconds ] = useState(15);
    useEffect(() => {
     const time = setInterval(() => {
        // setSeconds(seconds - 1);
        if(seconds === 0 && seconds !== -1) {
          console.log("Times Up!");
          if(currentQuestion !== 4){
            setCurrentQuestion(currentQuestion + 1);
            setSeconds(15);
            console.log(currentQuestion);
          } else {
            clearInterval(time);
            console.log("SHUP UP TIME!");

          }
        }
      }, 1000)
      if(currentQuestion <= 5 ){
        return () => clearInterval(time);
      } else {
        clearInterval(time);
      }
      
    })

  const checkAnswer = (isCorrect) => {
    if (isCorrect) {
      setAnswerCorrect('Right!');
      setScore(score + 100);
    } else {
      setAnswerCorrect('Wrong!');
    }
    const nextQuestion = currentQuestion + 1;
    if(nextQuestion < Quest.length) {
      setSeconds(15);
      setCurrentQuestion(nextQuestion);
      setshowAnswer(true);
    } else {
      setFinal(true);
    }
  };

  return (
    <>
      <List bulleted>
        {loading ? (
          <h1>Fetching data...</h1>
        ) : (
          <div>
            <h1>{currentQuestion + 1} / {Quest.length}</h1>
            {seconds}
            { showAnswer ? (
              <h4>{answerCorrect}</h4>
            ) : (
              <h4></h4>
            )}
            <List.Item key={Quest.id}>
              {Quest[currentQuestion].description}
              <List.List>
                    {Quest[currentQuestion].answer.map((answers) => {
                      return (
                        <Button
                          className="answerButton"
                          value={answers.id}
                          onClick={() => checkAnswer(answers.isCorrect)}
                          content={answers.option}
                          key={answers.id}
                        />
                      );
                    })
                  }
              </List.List>
            </List.Item>
            
          </div>
          //loading
         
        )}
      </List>
      { final ? (
            <Modal
      basic
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      size='small'
      trigger={setFinal(true)}
    >
      <Header icon>
        <Icon name='check' />
        100 / 500
      </Header>
      <Modal.Content>
        <p>
          Oh shoot!, looks like you need to study more.
          come back again when you feel like it.
        </p>
      </Modal.Content>
      <Modal.Actions>
        <Button basic color='red' inverted onClick={() => setOpen(false)}>
          <Icon name='remove' /> No
        </Button>
        <Button color='green' inverted onClick={() => setOpen(false)}>
          <Icon name='checkmark' /> Yes
        </Button>
      </Modal.Actions>
    </Modal>
          ) : (
            <h1></h1>
          )}
    </>
  );
}

export default Question;
