import React, { useState, useEffect, useRef, useContext } from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import { List, Button } from "semantic-ui-react";

import './question.css';

function Question() {

  const QUESTION_API = gql`
  {
    questions {
      id
      description
      answer {
        id
        option
        isCorrect
      }
    }
  }
`;

  const { loading, data: { questions: Quest } = {} } = useQuery(QUESTION_API);

  if(Quest) {
    console.log(Quest);
  }
  return (
    <>
      <List bulleted>
        {loading ? (
          <h1>Fetching data...</h1>
        ) : (
          Quest &&
          Quest.map((post) => (
            <div>
            <List.Item key={post.id}>
              {post.description}
              <List.List>
                {post.answer.map((answers) => {
                  return (
                    <Button value={answers.id} content={answers.option} key={answers.id} />
                  );
                })}
              </List.List>
            </List.Item>
            </div>
          ))
        )}
      </List>
    </>
  );
}

export default Question;
