import React, { useState, useEffect, useRef, useContext } from "react";
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import { List } from 'semantic-ui-react';

const QUESTION_API = gql `
    {
        questions{
            description
            answer{
              option
              points
            }
          }
    }
`;
function Question() {

    const { loading, data: {questions: Quest} ={} } = useQuery(QUESTION_API);
  return (
    <List bulleted>
        { loading ? (
            <h1>Fetching data</h1>
        ) : (
        <List.Item>
            {Quest.description}
            <List.List>
              <List.Item>{Question.answer}</List.Item>
            </List.List>
          </List.Item>
        )}
    
    <List.Item>Inviting Friends</List.Item>
    <List.Item>
      Benefits
      <List.List>
        <List.Item href='#'>Link to somewhere</List.Item>
        <List.Item>Rebates</List.Item>
        <List.Item>Discounts</List.Item>
      </List.List>
    </List.Item>
    <List.Item>Warranty</List.Item>
  </List>
  );
}

export default Question;
