import React, { useState, useEffect } from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
// import { List, Button, Card, Icon } from "semantic-ui-react";
import {
  Box,
  Grid,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
} from "@mui/material/";

import "./question.css";

function Question() {
  const QUESTION_API = gql`
    {
      questions {
        id
        description
        answer {
          id
          option
          isCorrect
        }
      }
    }
  `;

  // Run and fetch the data on the query
  const { loading, data: { questions: Quest } = {} } = useQuery(QUESTION_API);

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showAnswer, setshowAnswer] = useState(false);
  const [final, setFinal] = useState(false);
  const [answerCorrect, setAnswerCorrect] = useState(null);

  const [seconds, setSeconds] = useState(15);
  const [isRunning, setisRunning] = useState(true);

  useEffect(() => {
    if (isRunning) {
      const time = setInterval(() => {
        setSeconds((seconds) => seconds - 1);
        if (seconds === 0 && seconds >= -1) {
          if (currentQuestion >= 4) {
            setFinal(true);
            setisRunning(false);
            clearInterval(time);
          } else {
            setCurrentQuestion((currentQuestion) => currentQuestion + 1);
            setSeconds(15);
          }
        }
      }, 1000);
      return () => window.clearInterval(time);
    }
  });

  // a function to close the modal
  // const modalOff = () => {
  //   setFinal(false)
  // }

  const checkAnswer = (isCorrect) => {
    if (isCorrect) {
      setAnswerCorrect(true);
      setScore(score + 100);
    } else {
      setAnswerCorrect(false);
    }
    
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < Quest.length) {
      setSeconds(15);
      setCurrentQuestion(nextQuestion);
      setshowAnswer(true);
    } else {
      console.log(score);
      setFinal(true);
      setisRunning(false);
    }
  };

  return (
    <>
      {loading ? (
        <h2>fetching data!</h2>
      ) : (
        <Box
          className={"bg"}
          maxWidth={"60%"}
          marginLeft={"20%"}
          marginTop={"15%"}
          // minHeight={"50vh"}
          display={"flex"}
          flexDirection={"column"}
          justifyContent={"center"}
        >
          {final ? (
            <Dialog 
            open={final} 
            // onClose={modalOff} once clicks at the background outside the modal will close the modal
            >
              <DialogTitle>{"Congratulations!"}</DialogTitle>
              <DialogContent>
                <p>
                  You have finished on taking the quiz, and your final score is.
                </p>
                <h3 className="scoreStyle">{score} / 500</h3>
              </DialogContent>
            </Dialog>
          ) : (
            <>
              {seconds}
              {showAnswer ? (
                <h4
                  style={answerCorrect ? { color: "green" } : { color: "red" }}
                >
                  {answerCorrect ? "Correct!" : "Wrong!"}
                </h4>
              ) : (
                <></>
              )}
              <h2>
                {currentQuestion + 1} / <b>{Quest.length}</b>
              </h2>
              <h1>{Quest[currentQuestion].description}</h1>
              <Grid
                container
                rowSpacing={1}
                columnSpacing={{ xs: 1, sm: 6, md: 3 }}
              >
                {Quest[currentQuestion].answer.map((answers) => {
                  return (
                    <Grid item xs={6}>
                      <Button
                        variant="contained"
                        className="answerButton"
                        value={answers.id}
                        onClick={() => checkAnswer(answers.isCorrect)}
                        key={answers.id}
                        maxWidth={"100%"}
                      >
                        {answers.option}
                      </Button>
                    </Grid>
                  );
                })}
              </Grid>
            </>
          )}
        </Box>
      )}
    </>
  );
}

export default Question;
