import React, { useState, useEffect, useRef, useContext } from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
// import { List, Button, Card, Icon } from "semantic-ui-react";
import { Box, Grid, styled, Paper, Button } from "@mui/material";

import "./question.css";

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

function Question() {
  const QUESTION_API = gql`
    {
      questions {
        id
        description
        answer {
          id
          option
          isCorrect
        }
      }
    }
  `;

  // Run and fetch the data on the query
  const { loading, data: { questions: Quest } = {} } = useQuery(QUESTION_API);

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showAnswer, setshowAnswer] = useState(false);
  const [final, setFinal] = useState(true);
  const [answerCorrect, setAnswerCorrect] = useState(null);

  const [seconds, setSeconds] = useState(15);

  useEffect(() => {
    const time = setInterval(() => {
      setSeconds(seconds - 1);
      if (seconds === 0 && seconds !== -1) {
        console.log("Times Up!");
        if (currentQuestion !== 4) {
          setCurrentQuestion(currentQuestion + 1);
          setSeconds(15);
          console.log(currentQuestion);
        } else {
          return () => clearInterval(time);
          console.log("SHUP UP TIME!");
        }
      }
    }, 1000);
    if (currentQuestion <= 5) {
      return () => clearInterval(time);
    } else {
      return () => clearInterval(time);
    }
  });

  const checkAnswer = (isCorrect) => {
    if (isCorrect) {
      setAnswerCorrect(true);
      setScore(score + 100);
    } else {
      setAnswerCorrect(false);
    }
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < Quest.length) {
      setSeconds(15);
      setCurrentQuestion(nextQuestion);
      setshowAnswer(true);
    } else {
      console.log(score);
      setFinal(true);
    }
  };

  return (
    <>
      {loading ? (
        <h2>fetching data!</h2>
      ) : (
        <Box
        className={"bg"}
          maxWidth={"60%"}
          marginLeft={"20%"}
          marginTop={"15%"}
          minHeight={"50vh"}
          display={"flex"}
          flexDirection={"column"}
          justifyContent={"center"}
        >
          00:{seconds}
          {showAnswer ? <h4 style={answerCorrect ? {color: 'green'} : {color: 'red'}}>{answerCorrect ? "Correct!" : "Wrong!"}</h4> : <></>}
          <h2>{currentQuestion + 1} / <b>{Quest.length}</b></h2>
          <h1>{Quest[currentQuestion].description}</h1>
          <Grid
            container
            rowSpacing={1}
            columnSpacing={{ xs: 1, sm: 6, md: 3 }}
          >
            {Quest[currentQuestion].answer.map((answers) => {
              return (
                <Grid item xs={6}>
                  <Button
                    variant="contained"
                    className="answerButton"
                    value={answers.id}
                    onClick={() => checkAnswer(answers.isCorrect)}
                    key={answers.id}
                    maxWidth={"100%"}
                  >
                    {answers.option}
                  </Button>
                </Grid>
              );
            })}
          </Grid>
        </Box>
      )}
    </>
  );
}

export default Question;
