import React, { useState, useContext } from "react";
import { useMutation } from "@apollo/react-hooks";
import gql from "graphql-tag";
import {
  TextField,
  Button,
  Grid,
  Dialog,
  DialogTitle,
  DialogActions,
  DialogContent,
  DialogContentText,
} from "@mui/material";

import AuthContext from "../auth/AuthProvider";
import "./signup.css";

const SIGNUP_USER = gql`
  mutation signup($email: String!) {
    signUp(email: $email) {
      email
    }
  }
`;

function SignUp() {
  const { setAuth } = useContext(AuthContext);
  const [showForm, setshowForm] = useState(true);
  const [useEmail, setEmail] = useState("");

  const [addUser, { loading }] = useMutation(SIGNUP_USER, {
    update(proxy, result) {
      console.log(result);
    },
    variables: {
      email: useEmail,
    },
  });

  const inputChangeHandler = (input, value) => {
    setEmail(useEmail => {
      let isValid = true;
      for (const validator of useEmail.signupForm[TextField].validators) {
        isValid = isValid && validator(value);
      }
    })
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if(addUser()){
      setshowForm(false)
    }
  };

  return (
    <>
      {showForm ? (
        <div className="SignUpDiv">
          <form className="SignUpForm" onSubmit={handleSubmit}>
            <Grid container direction="column" justifyContent="space-between">
              <TextField
                required
                id="standard-required"
                onChange={inputChangeHandler}
                value={useEmail}
                label="Email Address"
                variant="standard"
              />
              <Button
                variant="contained"
                color="success"
                size="small"
                type="submit"
              >
                Enter
              </Button>
            </Grid>
          </form>
        </div>
      ) : (
        <Dialog open={!showForm}>
          <DialogTitle>Ready to take the Quiz?</DialogTitle>
          <DialogContent>
            <DialogContentText>
              The Quiz contains <b>5 Questions</b>  with <b>4 options</b>  with <b>1 correct answer</b>
              <br />
              You will be given a <b>15 seconds</b> to answer each question.
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button>Enter</Button>
          </DialogActions>
        </Dialog>
      )}
    </>
  );
}

export default SignUp;
