import React, { useState, useEffect, useRef, useContext } from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import { Card, List } from "semantic-ui-react";

import './question.css';

function Question() {

  const QUESTION_API = gql`
  {
    questions {
      id
      description
      answer {
        id
        option
        isCorrect
      }
    }
  }
`;

  const { loading, data: { questions: Quest } = {} } = useQuery(QUESTION_API);

  return (
    <>
      <List bulleted>
        {loading ? (
          <h1>Fetching data...</h1>
        ) : (
          Quest &&
          Quest.map((post) => (
            <div>
            <List.Item key={post.id}>
              {post.description}
              <List.List>
                {post.answer.map((answers) => {
                  return (
                      
                    <a href={answers.id}>
                        <List.Item className="List-Item" key={answers.id}>
                      {answers.option}
                    </List.Item>
                    </a>
                  );
                })}
              </List.List>
            </List.Item>
            </div>
          ))
        )}
      </List>
    </>
  );
}

export default Question;
